@bot.event
async def on_command_error(ctx, error):
    if isinstance(error, commands.errors.MissingRequiredArgument):
        await ctx.send(
            embed=discord.Embed(
                title="Uh oh",
                color=discord.Color.red(),
                description=(
f'''
Arguments missing or incorrect syntax
Use `$help <command>` to learn the syntax\n
`{error}`
''' 
                )
            )
        )
    else: raise error


